import axios from 'axios';

const API_KEY = 'a651cb49'; // Replace with your API key
const BASE_URL = 'https://www.omdbapi.com';

export const searchMovies = async (query, page = 1, type = '') => {
  try {
    const response = await axios.get(`${BASE_URL}/?apikey=${API_KEY}&s=${query}&page=${page}${type ? `&type=${type}` : ''}`);
    return response.data;
  } catch (error) {
    throw new Error('Failed to fetch movies');
  }
};

export const getMovieDetails = async (imdbID) => {
  try {
    const response = await axios.get(`${BASE_URL}/?apikey=${API_KEY}&i=${imdbID}&plot=full`);
    return response.data;
  } catch (error) {
    throw new Error('Failed to fetch movie details');
  }
};
